def factorial(n):
    if n == 0:
        return 1
    return n * factorial(n-1)

def sqrt(n):
    low = 0
    high = n

    while low <= high:
        mid = (low + high) // 2
        if mid * mid == n:
            return mid
        elif mid * mid < n:
            low = mid + 1
        else:
            high = mid - 1

    return low - 1

def isRotation(s1, s2):
    if len(s1) != len(s2):
        return False

    s3 = s1 + s1 
    return s3.find(s2) != -1

