def gcd(a, b):
  while b:
    a, b = b, a % b
  return a

def lcm(a, b):
  return (a * b) // gcd(a, b)

def is_lcm(num1, num2):
  """
  Checks if the least common multiple of two integers is valid.
  """
  if num1 == 0 or num2 == 0:
    return False
  return lcm(num1, num2) == num1 * num2

