int main() {
  int i, j;

  // Print the elements of the product table
  printf("Product Table:\n");

  // Loop through the rows of the table
  for (i = 1; i <= 3; i++) {
    // Loop through the columns of the table
    for (j = 1; j <= 3; j++) {
      // Calculate the product of the current row and column
      int product = i * j;

      // Print the product
      printf("(%d) * (%d) = %d\n", i, j, product);
    }
  }

  return 0;
}
