def convert(sentence):
    words = sentence.split()
    new_sentence = ""
    for word in words:
        if word.endswith("s"):
            new_word = word[:-1] + "es"
        else:
            new_word = word
        new_sentence += new_word + " "
    return new_sentence.strip()

# Example usage
