CREATE PROCEDURE ReverseLinkedList(input_list_head)
BEGIN
    DECLARE node1 node;
    DECLARE node2 node;
    DECLARE temp node;

    SELECT @node1 = * INTO node1 FROM input_list_head;
    SELECT @node2 = * INTO node2 FROM input_list_head;

