        @node1 = @node2;
        @node2 = temp;

        SELECT @node1 = * INTO node1 FROM input_list_head;
        SELECT @node2 = * INTO node2 FROM input_list_head;
    END WHILE;

    SELECT * FROM input_list_head;
END;
