function tweet(message, hashtag, callback) {
  var tweet = {
    message: message,
    hashtag: hashtag
  };

  var options = {
    consumer_key: config.consumer_key,
    consumer_secret: config.consumer_secret,
    access_token: config.access_token,
    access_token_secret: config.access_token_secret
  };

  var client = new TwitterOAuth(options);

  client.post('/1.1/statuses/update.json', tweet, function(error, response, body) {
