CREATE FUNCTION sort_strings_alphabetically (input_list TEXT[]) RETURNS TEXT[] AS $$
BEGIN
  -- Sort the input list alphabetically
  FOR i IN 1..LENGTH(input_list) LOOP
    FOR j IN 1..LENGTH(input_list) LOOP
      IF input_list[j] > input_list[i] THEN
        -- Swap the strings
        input_list[j], input_list[i] = input_list[i], input_list[j];
      END IF;
    END LOOP;
  END LOOP;
  -- Return the sorted list
  RETURN input_list;
END;
$$ LANGUAGE plpgsql;
