function findLongestWord(sentence) {
  let max_length = 0;
  let max_word = "";

  for (let word of sentence) {
    let wordLength = word.length;
    if (wordLength > max_length) {
      max_length = wordLength;
      max_word = word;
    }
  }

  return max_word;
}
